
import { useState } from "react";
// Poster image path
import poster from "/assets/upcoming/publicPros.png";
import { jsPDF } from "jspdf";
import QRCodeGen from "qrcode-generator";

const EventRegistration = () => {
  const [formData, setFormData] = useState({
    name: "",
    studentId: "",
    discipline: "",
    batch: "",
    email: "",
    contact: "",
    segments: [], // multi-select via checkboxes
  });

  const [submitted, setSubmitted] = useState(false); // <--- add this
  const [submittedData, setSubmittedData] = useState(null); // for PDF download

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name === "segments") {
      if (checked) {
        setFormData({
          ...formData,
          segments: [...formData.segments, value],
        });
      } else {
        setFormData({
          ...formData,
          segments: formData.segments.filter((seg) => seg !== value),
        });
      }
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // <-- Replace with your current Apps Script web app URL (ends with /exec) -->
    const googleScriptURL =
      "https://script.google.com/macros/s/AKfycby88jFdDJsOMK30AkHoxa04Zq18p6rhjmeYZAdvCUQ_54shxUqmNv5ajqXdYVugqDg9wA/exec";

    // Build FormData (this avoids preflight + is accepted by Google Apps Script as e.parameter / e.parameters)
    const fd = new FormData();
    fd.append("name", formData.name);
    fd.append("studentId", formData.studentId);
    fd.append("discipline", formData.discipline);
    fd.append("batch", formData.batch);
    fd.append("email", formData.email);
    fd.append("contact", formData.contact);

    // Append segments separately — multiple entries with same key
    formData.segments.forEach((seg) => fd.append("segments", seg));

    // Use mode: "no-cors" so browser doesn't preflight (Apps Script web apps behave better without preflight).
    // Note: with no-cors we cannot read response body — but server will still process the data.
    fetch(googleScriptURL, {
      method: "POST",
      body: fd,
      mode: "no-cors",
    })
      .then(() => {
        setSubmitted(true);
        setSubmittedData(formData); // store submitted data for PDF
        setFormData({
          name: "",
          studentId: "",
          discipline: "",
          batch: "",
          email: "",
          contact: "",
          segments: [],
        });
      })
      .catch((err) => {
        console.error("Fetch error:", err);
        alert("Error submitting the form. Please try again.");
      });
  };



 



if (submitted) {
      


const downloadConfirmation = () => {
  if (!submittedData) return;

  const doc = new jsPDF({
    orientation: "landscape",
    unit: "pt",
    format: [400, 200],
  });

  // Colors
  const primary = [37, 99, 235];
  const accent = [59, 130, 246];
  const bg = [245, 247, 250];

  // Background
  doc.setFillColor(...bg);
  doc.rect(0, 0, 400, 200, "F");

  // Header gradient effect
  for (let i = 0; i < 40; i++) {
    const r = primary[0] + ((accent[0] - primary[0]) * i) / 40;
    const g = primary[1] + ((accent[1] - primary[1]) * i) / 40;
    const b = primary[2] + ((accent[2] - primary[2]) * i) / 40;
    doc.setFillColor(r, g, b);
    doc.rect(0, i, 400, 1, "F");
  }

  // Logo
  const logoBase64 = "data:image/png;base64,"; // Add your logo Base64 here
  if (logoBase64) doc.addImage(logoBase64, "PNG", 15, 5, 30, 30); // bigger logo

  // Header text
  doc.setFont("helvetica", "bold");
  doc.setFontSize(18);
  doc.setTextColor(255, 255, 255);
  doc.text("Public Speaking Pros 3.0", 200, 27, { align: "center" });

  // Ticket area
  doc.setDrawColor(220, 220, 230);
  doc.roundedRect(20, 50, 360, 100, 8, 8, "S");
  doc.setFillColor(255, 255, 255);
  doc.roundedRect(22, 52, 356, 96, 8, 8, "F");

  // Divider (left/right)
  doc.setDrawColor(230, 230, 230);
  doc.line(230, 52, 230, 148);

  // --- Left Side: Participant Info ---
  doc.setTextColor(33, 33, 33);
  doc.setFont("helvetica", "bold");
  doc.setFontSize(13);
  doc.text("Participant Details", 40, 65);

  doc.setFont("helvetica", "normal");
  doc.setFontSize(12);
  const infoStartY = 85;
  const lineHeight = 18;

  doc.text(`Name: ${submittedData.name}`, 40, infoStartY);
  doc.text(`Student ID: ${submittedData.studentId}`, 40, infoStartY + lineHeight);
  doc.text(`Discipline: ${submittedData.discipline}`, 40, infoStartY + lineHeight * 2);
  doc.text(`Segment: ${submittedData.segments.join(", ")}`, 40, infoStartY + lineHeight * 3);

  // --- Right Side: QR Code for auto-attendance ---
// Prepare readable text for QR code
const qrText = `
Public Speaking Pros 3.0

Participant Details:
Name: ${submittedData.name}
Student ID: ${submittedData.studentId}
Discipline: ${submittedData.discipline}
Batch: ${submittedData.batch}
Email: ${submittedData.email}
Contact: ${submittedData.contact}
Segment(s): ${submittedData.segments.join(", ")}
`.trim();

// Generate QR code
const qr = QRCodeGen(0, "L");
qr.addData(qrText);
qr.make();
const qrBase64 = qr.createDataURL(8);



// --- Positioning for the QR code on PDF ---
const qrWidth = 80;
const qrHeight = 80;
const qrX = 230 + (130 - qrWidth) / 2;
const qrY = 52 + (96 - qrHeight) / 2;
doc.addImage(qrBase64, "PNG", qrX, qrY, qrWidth, qrHeight);


  // --- Footer ---
  doc.setFillColor(...primary);
  doc.rect(0, 170, 400, 30, "F");

  doc.setTextColor(255, 255, 255);
  doc.setFont("helvetica", "bold");
  doc.setFontSize(10);
  doc.text(
    "Thank you for registering — See you there!",
    200,
    188,
    { align: "center" }
  );

  doc.save(`PublicSpeakingPros3.0_${submittedData.studentId}.pdf`);
};



        

      

  





  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-green-200 via-green-100 to-blue-200 py-10 px-4">
      <div className="max-w-xl w-full bg-white rounded-2xl shadow-xl p-8 text-center">
        <h1 className="text-3xl font-bold text-green-700 mb-4">
          🎉 Congratulations! Registration Successful
        </h1>
        <p className="text-gray-700 mb-6">
          Thank you for registering for <b>Public Speaking Pros 3.0</b>.  
          We’ll contact you soon with further details.
        </p>
        <div className="flex flex-col gap-3">
          <button
            onClick={downloadConfirmation}
            className="w-full bg-gradient-to-r from-blue-500 to-purple-500 text-white font-semibold py-3 rounded-lg hover:from-blue-600 hover:to-purple-600 transition-all"
          >
            Download Confirmation
          </button>
          <button
            onClick={() => setSubmitted(false)}
            className="w-full bg-gray-200 text-gray-700 font-semibold py-3 rounded-lg hover:bg-gray-300 transition-all"
          >
            Register Another
          </button>
          <a
            href="/"
            className="w-full bg-gray-200 text-gray-700 font-semibold py-3 rounded-lg hover:bg-gray-300 transition-all"
          >
            Go to Homepage
          </a>
        </div>
      </div>
    </div>
  );
}


 return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-blue-200 via-blue-100 to-purple-200 py-10 px-4">
      <div className="max-w-3xl w-full bg-white rounded-2xl shadow-xl p-6 md:p-10">
        <div className="mb-6">
          <img src={poster} alt="Public Speaking Pros 3.0" className="w-full rounded-lg shadow-md" />
        </div>

        <h1 className="text-3xl md:text-4xl font-bold text-center mb-2 text-gray-800">
          Public Speaking Pros 3.0 Registration
        </h1>
        <p className="text-center text-gray-600 mb-8">
          Fill out the form below to register. Upload your video to complete registration.
        </p>

        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Text Inputs */}
          {["name", "studentId", "discipline"].map((field) => (
            <div key={field}>
              <label className="block text-gray-700 font-medium mb-1">
                {field === "name" ? "Full Name" : field === "studentId" ? "Student ID" : "Discipline"}
              </label>
              <input
                type="text"
                name={field}
                value={formData[field]}
                onChange={handleChange}
                required
                className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>
          ))}

          {/* Batch */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Batch</label>
            <select
              name="batch"
              value={formData.batch}
              onChange={handleChange}
              required
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            >
              <option value="">Select your batch</option>
              <option value="24">24</option>
              <option value="25">25</option>
            </select>
          </div>

          {/* Email */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Email Address</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
          </div>

          {/* Contact */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Contact Number</label>
            <input
              type="tel"
              name="contact"
              value={formData.contact}
              onChange={handleChange}
              required
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
          </div>

          {/* Segments */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">Segment (Select one or more)</label>
            <div className="flex flex-wrap gap-4">
              {["English", "Bengali"].map((seg) => (
                <label key={seg} className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="segments"
                    value={seg}
                    checked={formData.segments.includes(seg)}
                    onChange={handleChange}
                    className="h-4 w-4"
                  />
                  <span className="text-gray-700">{seg}</span>
                </label>
              ))}
            </div>
          </div>

          {/* Video Upload */}
          {/* <div>
            <label className="block text-gray-700 font-medium mb-1">Upload Video (Required)</label>
            <input
              type="file"
              name="video"
              accept="video/*"
              onChange={handleChange}
              required
              className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            {uploadProgress > 0 && uploadProgress < 100 && (
              <p className="text-sm text-blue-600 mt-1">Uploading... {uploadProgress}%</p>
            )}
            {uploadProgress === 100 && <p className="text-sm text-green-600 mt-1">✅ Upload complete!</p>}
          </div> */}

          <button
            type="submit"
            className="w-full bg-gradient-to-r from-blue-500 to-purple-500 text-white font-semibold py-3 rounded-lg hover:from-blue-600 hover:to-purple-600 transition-all"
          >
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default EventRegistration;